/*custom.min*/
	
jQuery(document).ready(function($){

	//height of the container
	//var vg_content_height = $('#vg-container').height();
	//$('#vg-container').css({ 'height':vg_content_height });
	
	//portfolio - show link
	$('.vg-background').hover(
		function () {
			$(this).animate({opacity:'1'});
		},
		function () {
			$(this).animate({opacity:'0'});
		}
	);
	
	//portfolio - filter
	$('.vg-filter').click(
		function () {
			
			//remove active
			var active_class = $( '.vg-filtering' ).find( '.vg-pages-active' );
			$( active_class ).removeClass( 'vg-pages-active' );
			
			//add active class to clicked link
			$( this ).addClass( 'vg-pages-active' );
			
			$( '.portfolio' ).fadeOut(); //you can use slideUp() instead
			var category = $(this).attr('data-category');
			$( '.' + category ).fadeIn(); //you can use slideDown() instead
			
			//show all when clicked "ALL" filter
			if( category == 'all' ){
				$( '.portfolio' ).fadeIn(); //you can use slideDown() instead
			}
			//alert( category );
			return false;
		}
	);
	
	// Portfolio - change image preview //
	$('.vg-preview').click(
		function () {
			
			var active_class = $( '.vg-project-3' ).find( '.vg-pages-active' );
			$( active_class ).removeClass( 'vg-pages-active' );
			$( active_class ).addClass( 'vg-next-img-normal' );
			
			$( this ).removeClass( 'vg-next-img-normal' );
			$( this ).addClass( 'vg-pages-active' );
			
			var image = $(this).attr('data-image');
			$('#vg-current-image').attr("src",image);
			return false;
			
		}
	);
	
	//slideshow
	$(function() {
		$(".slidetabs").tabs(".vg-slideshow > .vg-slide", {
			effect: 'fade', //transition. 'fade' or 'slide'
			fadeOutSpeed: 'slow', //set the speed. 'fast', 'slow' or in miliseconds like 5000
			rotate: true,
			autopause:false
		})
		.slideshow({
			autoplay:true,//set autoplay. true or false
			interval: 7000//set interval
		});
	});
	
	//twitter
	jQuery(function($){
        $("#ticker").tweet({
          username: "envato", //set the twitter user here
          page: 1,
          avatar_size: 32,
          count: 20,
          loading_text: "loading ..."
        }).bind("loaded", function() {
          var ul = $(this).find(".tweet_list");
          var ticker = function() {
            setTimeout(function() {
              ul.find('li:first').animate( {marginTop: '-4em'}, 500, function() {
                $(this).detach().appendTo(ul).removeAttr('style');
              });
              ticker();
            }, 5000);
          };
          ticker();
        });
     });
	
	//tabs
	//$(".tab-link").tabs(".tab-content > .vg-tab", {
		//current: 'vg-tab-active',
		//effect: 'fade', //set the transition: 'fade', 'slide'
		/*fadeOutSpeed: 'slow', //set speed: 'slow', 'fast' or in miliseconds like 5000*/
	//});
	
	$("div.vg-tabs").tabs("div.vg-panes > div", {
		current: 'vg-tab-active',
		effect: 'fade'
	});
	
});
